    const messageText = messageInput.value.trim();
    if (messageText !== '') {
        const messageId = db.ref('messages').push().key;
        db.ref(`messages/${messageId}`).set({
            text: messageText,
            timestamp: Date.now()
        });
        messageInput.value = '';
    }
}

// Function to create a group
function createGroup() {
    const groupName = groupNameInput.value.trim();
    if (groupName !== '') {
        const groupId = db.ref('groups').push().key;
        db.ref(`groups/${groupId}`).set({
            name: groupName,
            members: []
        });
        groupNameInput.value = '';
    }
}

// Function to add a contact
function addContact() {
    const contactName = contactNameInput.value.trim();
    if (contactName !== '') {
        const contactId = db.ref('contacts').push().key;
        db.ref(`contacts/${contactId}`).set({
            name: contactName
        });
        contactNameInput.value = '';
    }
}

// Function to render the chat list
